    die();
}

/**
 * Validate IP, Port or Host
 *
 * @param $address
 *
 * @return bool
 */
function isValidIpPort($address): bool
{
    if ((filter_var($address, FILTER_VALIDATE_IP) || filter_var($address, FILTER_VALIDATE_URL) || $address === 'localhost')) {
        return true;
    } elseif (preg_match('~\b([0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}):([0-9]{1,10}\b)~', $address, $matches)) {
        if (! preg_match('/^([0-9]{1,4}|[1-5][0-9]{4}|6[0-4][0-9]{3}|65[0-4][0-9]{2}|655[0-2][0-9]|6553[0-5])$/m', $matches[2], $arraystr)) {
            return false;
        }

        return true;
    }

    return false;
}

$target    = '../installation/';
$canRemove = [];

if (is_dir($target)) {
    $files = glob('*', GLOB_MARK);

    foreach ($files as $file) {
        if (! is_writeable($file)) {
            $canRemove[] = $file;
        }
        if (is_dir($file)) {
            $files = glob($file . '*', GLOB_MARK);
            foreach ($files as $file) {
                if (! is_writeable($file)) {
                    $canRemove[] = $file;
                }
            }
        }
    }
}

if (isset($_POST['dbc'])) {
    $data   = json_decode($_POST['dbc'], true);
    $status = [];
    $cred   = [
        'CMS'   => ['host' => $data[0]['value'], 'user' => $data[2]['value'], 'pass' => $data[3]['value'], 'db' => $data[1]['value']],
        'AUTH'  => ['host' => $data[4]['value'], 'user' => $data[6]['value'], 'pass' => $data[7]['value'], 'db' => $data[5]['value']],
        'WORLD' => ['host' => $data[8]['value'], 'user' => $data[10]['value'], 'pass' => $data[11]['value'], 'db' => $data[9]['value']]
    ];

    $mysqli = new mysqli();
    $mysqli->set_opt(MYSQLI_OPT_READ_TIMEOUT, 3);

    foreach ($cred as $key => $check) {
        if (isValidIpPort($check['host']) && ! empty($check['user']) && ! empty($check['pass']) && ! empty($check['db'])) {
            $mysqli->connect($check['host'], $check['user'], $check['pass'], $check['db']);
            if (! $mysqli->connect_error) {
                $status[$key] = true;
            } else {
                $status[$key] = false;
            }
            $mysqli->close();
        } else {
            $status[$key] = false;
        }
    }

    echo json_encode($status);
    exit;
}

if ($_POST) {
    $core->setInput($_POST);

    if ($core->reWrite()) {
        $core->removeFiles($target);
    }
}

