<script>
    (function ($) {
        $.fn.replaceClass = function (pFromClass, pToClass) {
            this.removeClass(pFromClass)
            setTimeout(() => {
                this.addClass(pToClass);
            });
            return this;
        };
    }(jQuery));

    let msg_success = '<i class="fas fa-circle-check"></i> Succesfully Connected. You are good to go!';
    let msg_fail = '<i class="fas fa-circle-exclamation"></i> Failed to Connect. Please check credentials!';

    $('#form_checkdb').on('submit', e => {
        e.preventDefault();
        let dbc = $("#form_install").serializeArray();

        dbc = dbc.filter(function (elem) {
            return elem.name !== 'language' && elem.name !== 'theme' && elem.name !== 'cookie_secure';
        });

        dbc = JSON.stringify(dbc);

        $.ajax({
            type: 'post',
            data: {
                dbc
            },
            dataType: 'json',
            success: function (response) {

                if (response.CMS === true) {
                    $('#s_cms').html(msg_success);
                    $('#s_cms').replaceClass('fail notify', 'success notify');
                } else {
                    $('#s_cms').html(msg_fail);
                    $('#s_cms').replaceClass('success notify', 'fail notify');
                }

                if (response.AUTH === true) {
                    $('#s_auth').html(msg_success);
                    $('#s_auth').replaceClass('fail notify', 'success notify');
                } else {
                    $('#s_auth').html(msg_fail);
                    $('#s_auth').replaceClass('success notify', 'fail notify');
                }

                if (response.WORLD === true) {
                    $('#s_world').html(msg_success);
                    $('#s_world').replaceClass('fail notify', 'success notify');
                } else {
                    $('#s_world').html(msg_fail);
                    $('#s_world').replaceClass('success notify', 'fail notify');
                }

                if (response.CMS === true && response.AUTH === true && response.WORLD === true) {
                    $('#btn_c').attr('disabled', 'disabled');
                    $('#i_w').toggleClass('fa-spin');
                    $('#btn_w').removeAttr('disabled');
                }
            }
        });
    });

    $body = $("body");

    $(document).on({
        ajaxStart: function () {
            $body.addClass("loading");
        },
        ajaxStop: function () {
            $body.removeClass("loading");
        }
    });
</script>
